CREATE OR REPLACE FUNCTION public.set_old_som_not_current()
    RETURNS trigger
    LANGUAGE 'plpgsql'
    COST 100
    VOLATILE NOT LEAKPROOF
AS $BODY$
begin
  update public.soms
  set current=false
  where proposal_id = new.proposal_id
  and milestone = new.milestone
  and current
  and id != new.id;
  return new;
end;
$BODY$;

