CREATE OR REPLACE FUNCTION public.is_in_challenge_ct(
	_challenge_ids bigint[])
    RETURNS boolean
    LANGUAGE 'sql'
    COST 100
    VOLATILE SECURITY DEFINER PARALLEL UNSAFE
AS $BODY$
SELECT EXISTS (
  SELECT cu.user_id
  FROM public.challenges_users cu
  WHERE (
    cu.user_id = auth.uid()
    AND cu.challenge_id = any (_challenge_ids)
  )
);
$BODY$;

